import moment from 'moment';
/**
 * 时间展示类型改变事件
 * @param val
 */

export var changeDateFormat = function changeDateFormat(val, modeType) {
  var dateFormat = '';

  switch (modeType) {
    case 'datetime':
      dateFormat = moment(val).format('YYYY-MM-DD HH:mm');
      break;

    case 'month':
      dateFormat = moment(val).format('YYYY-MM');
      break;

    case 'time':
      dateFormat = moment(val).format('hh:mm');
      break;

    case 'year':
      dateFormat = moment(val).format('YYYY');
      break;

    default:
      dateFormat = moment(val).format('YYYY-MM-DD');
      break;
  }

  return dateFormat;
};
export var dateChange = function dateChange(date) {
  var stringDate = moment(date).format('YYYY-MM-DD-HH-mm-ss');
  var dateList = stringDate.split('-');
  var numberDateList = dateList.map(function (item) {
    return parseInt(item, 10);
  });
  return new Date(numberDateList[0], numberDateList[1] - 1, numberDateList[2], numberDateList[3], numberDateList[4], numberDateList[5]);
};
export var getByteLen = function getByteLen(val) {
  var len = 0;
  val.split('').forEach(function (item) {
    // eslint-disable-next-line no-control-regex
    if (item.match(/[^\x00-\xff]/gi) != null) {
      len += 2;
    } else {
      len += 1;
    }
  });
  return len;
};
