import { createMemoryHistory } from '/Users/chencheng/Code/github.com/umijs/plugins/node_modules/@umijs/runtime';

let options = {
  "initialEntries": [
    "/"
  ]
};
if ((<any>window).routerBase) {
  options.basename = (<any>window).routerBase;
}

let history = createMemoryHistory(options);
export const createHistory = (hotReload = false) => {
  if (!hotReload) {
    history = createMemoryHistory(options);
  }

  return history;
};

// 通常仅微前端场景需要调用这个 API
export const setCreateHistoryOptions = (newOpts: any = {}) => {
  options = { ...options, ...newOpts };
};

export { history };
